import React, { useState, useEffect } from 'react';
import './App.css';

function Video({ video, index }) {
  return (
    <div className="video-item" key={index}>
      <video className="video-thumbnail" controls>
        <source src={video.link} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="video-info">
        <h3 className="video-title">{video.name || `Video ${index + 1}`}</h3>
        <p className="video-metadata">Channel Name • 1.2M views • 2 days ago</p>
      </div>
    </div>
  );
}

function Garage() {
  const [vids, setVids] = useState([
    { name: "Sample Video 1", link: "./uploads/0001-0030.mp4" },
    { name: "Sample Video 2", link: "./uploads/0001-0250.mp4" },
    { name: "Sample Video 3", link: "./uploads/3.mp4" },
    { name: "Sample Video 4", link: "./uploads/2.mp4" },
    { name: "Sample Video 5", link: "./uploads/5.mp4" },
    { name: "Sample Video 6", link: "/uploads/0001-0072.mp4" },
  ]);

  useEffect(() => {
    fetch('http://localhost:3001/api/data')
      .then((response) => response.json())
      .then((result) => {
        const formattedVids = result.response.map((link, index) => ({
          name: `Fetched Video ${index + 1}`,
          link,
        }));
        setVids(formattedVids);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="videos-grid">
      {vids.map((video, index) => (
        <Video key={index} video={video} index={index} />
      ))}
    </div>
  );
}

function App() {
  return (
    <div>
      <header>
        <a href="./upload.html">Upload Video</a>
        <h1>Yubtub</h1>
      </header>
      <h2>Recommended Videos</h2>
      <Garage />
    </div>
  );
}

export default App;
